/*global tinymce:true */

tinymce.PluginManager.add('eqdkp_easyinsert', function(editor) {
	function showDialog() {
		var win, data, dom = editor.dom;
		var width, height, imageListCtrl;

		win = editor.windowManager.open({
			title: "Insert JavaScript",
			data: data,
			body: [
				{name: 'type', type: 'listbox', label: 'Select Type', values: [{text: 'Tabs', value: 'tabs'}, {text: 'Accordion', value: 'accordion'}, {text: 'Spoiler', value: 'spoiler'}, {text: 'Progressbar', value: 'progressbar'}]},
			],
			onSubmit: function(e) {
				var data = e.data;
				
				var random = "easyinsert_" + Math.floor((Math.random() * 10000) + 1);
				
				if (data.type == 'tabs'){
					editor.execCommand('mceInsertContent', false, '<script>$(document).ready(function(){ $(function() {$( "#tabs_'+random+'" ).tabs();}); })</script> <div id="tabs_'+random+'" class="system-block2"><ul><li><a href="#tabs-1">Tab 1</a></li><li><a href="#tabs-2">Tab 2</a></li><li><a href="#tabs-3">Tab 3</a></li></ul><div id="tabs-1"><p>Content Tab 1</p></div><div id="tabs-2"><p>Content Tab 2</p></div><div id="tabs-3"><p>Content Tab 3</p></div></div><p>&nbsp;</p>');
				}
				
				if (data.type == 'accordion'){
					editor.execCommand('mceInsertContent', false, '<script>$(document).ready(function(){ $(function() { $( "#accordion_'+random+'" ).accordion(); }); })</script> <div id="accordion_'+random+'" class="system-block2"><h3>Section 1</h3><div><p>Content Section 1</p></div><h3>Section 2</h3><div><p>Content Section 2</p></div><h3>Section 3</h3><div><p>Content Section 3</p></div></div><p>&nbsp;</p>');
				}
				
				if (data.type == 'spoiler'){
					editor.execCommand('mceInsertContent', false, '<script>$(document).ready(function(){ $("#spoiler_btn_'+random+'").parent().find(".spoiler_content").hide();  $("#spoiler_btn_'+random+'").on("click", function(){ $(this).parent().find(".spoiler_content").toggle(); if($(this).parent().find(".spoiler_content").is(":visible")){ $(this).html("Hide text"); } else { $(this).html("Show text");}})})</script><div class="spoiler_container system-block2"><button class="spoiler_button" id="spoiler_btn_'+random+'">Show text</button><div class="spoiler_content">Hidden Content</div></div><p>&nbsp;</p>');
				}
				
				if (data.type == 'progressbar'){
					editor.execCommand('mceInsertContent', false, '<script> $(document).ready(function(){ $(function() { $( "#progressbar_'+random+'" ).progressbar({ value: 42 });});});</script>Progessbar Title<div class="system-block2" id="progressbar_'+random+'"></div><p>&nbsp;</p>');
				}

			}
		});
	}

	editor.addMenuItem('eqdkp_easyinsert', {
		icon: '',
		text: 'Insert JavaScript',
		onclick: showDialog,
		context: 'insert',
		prependToContext: true
	});
});